import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NoteList = ({ userId }) => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Gửi yêu cầu GET để lấy danh sách các ghi chú của người dùng
    axios.get(`/api/notes/${userId}`)
      .then(response => {
        setNotes(response.data);
      })
      .catch(error => {
        console.error('Failed to fetch notes', error);
      });
  }, [userId]);

  const handleDeleteNote = (noteId) => {
    // Gửi yêu cầu DELETE để xoá ghi chú
    axios.delete(`/api/notes/${userId}/${noteId}`)
      .then(() => {
        // Cập nhật danh sách ghi chú sau khi xoá
        setNotes(prevNotes => prevNotes.filter(note => note._id !== noteId));
      })
      .catch(error => {
        console.error('Failed to delete note', error);
      });
  };

  return (
    <div>
      <h2>Danh sách ghi chú</h2>
      <ul>
        {notes.map(note => (
          <li key={note._id}>
            {note.content}{' '}
            <button onClick={() => handleDeleteNote(note._id)}>Xoá</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default NoteList;
