import React from 'react';
import NoteList from './NoteList';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      notes: [], // Danh sách ghi chú ban đầu là rỗng
      newNote: '', // Trạng thái để lưu ghi chú mới
    };
  }

  handleAddNote = () => {
    const { notes, newNote } = this.state;
    if (newNote.trim() === '') return; // Kiểm tra ghi chú mới có giá trị không trống
    this.setState({
      notes: [...notes, newNote], // Thêm ghi chú mới vào danh sách
      newNote: '', // Xóa nội dung ghi chú mới
    });
  };

  render() {
    return (
      <div>
        <h1>Ghi chú</h1>
        <input
          type="text"
          value={this.state.newNote}
          onChange={(e) => this.setState({ newNote: e.target.value })}
        />
        <button onClick={this.handleAddNote}>Thêm</button>
        <NoteList notes={this.state.notes} />
      </div>
    );
  }
}

export default App;
