import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Layout, Menu } from 'antd';
import './App.css';
import NoteList from './components/NoteList';
import LoginForm from './components/LoginForm';

const { Header, Content, Footer } = Layout;

function App() {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/notes');
        setNotes(response.data);
      } catch (error) {
        console.error('Lỗi tìm nạp ghi chú:', error);
      }
    };

    fetchData();
  }, []);

  const handleAddNote = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/notes', { text: newNote });
      setNotes([...notes, response.data]);
      setNewNote('');
    } catch (error) {
      console.error('Lỗi khi thêm ghi chú:', error);
    }
  };

  const handleDeleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/notes/${id}`);
      const updatedNotes = notes.filter((note) => note._id !== id);
      setNotes(updatedNotes);
    } catch (error) {
      console.error('Lỗi xóa ghi chú:', error);
    }
  };

  const handleDeleteAllNotes = async () => {
    try {
      await axios.delete('http://localhost:3001/api/notes');
      setNotes([]);
    } catch (error) {
      console.error('Lỗi xóa tất cả ghi chú:', error);
    }
  };

  const handleLogin = () => {
    // Xử lý đăng nhập ở đây và sau khi đăng nhập thành công, gọi setLoggedIn(true);
    setLoggedIn(true);
  };

  return (
    <Layout>
      <Header>
        {/* Đặt menu và header ở đây */}
      </Header>
      <Content className="site-layout" style={{ padding: '20px 50px' }}>
        {loggedIn ? (
          // Hiển thị danh sách ghi chú khi đã đăng nhập
          <div>
            <Input.TextArea
              placeholder="Thêm lưu chú mới"
              value={newNote}
              onChange={(e) => setNewNote(e.target.value)}
              size="large"
            />
            <Button type="primary" onClick={handleAddNote}>
              Thêm
            </Button>
            <Button onClick={handleDeleteAllNotes} type="primary" danger className="delete-button">
              Xoá Tất Cả
            </Button>
            {/* Nút thêm và danh sách ghi chú */}
            <NoteList notes={notes} handleDeleteNote={handleDeleteNote} />
          </div>
        ) : (
          // Hiển thị form đăng nhập khi chưa đăng nhập
          <LoginForm handleLogin={handleLogin} />
        )}
      </Content>
      <Footer style={{ textAlign: 'center' }}>Ant Design ©2023 Created by BaoTrong</Footer>
    </Layout>
  );
}

export default App;
