// App.js

import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import { Layout, Menu } from 'antd';
import Login from './components/Login'; // Import component Đăng nhập

const { Header, Content, Footer } = Layout;

function App() {
  return (
    <Router>
      <Layout>
        <Header
          style={{
            position: 'sticky',
            top: 0,
            zIndex: 1,
            width: '100%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
          }}
        >
          <Menu
            theme="dark"
            mode="horizontal"
            defaultSelectedKeys={['1']}
            items={new Array(1).fill(null).map((_, index) => ({
              key: String(index + 1),
              label: `Lưu chú`,
            }))}
          />
        </Header>
        <Content className="site-layout" style={{ padding: '20px 50px' }}>
          <Switch>
            <Route path="/login">
              <Login /> {/* Component Đăng nhập */}
            </Route>
            <Route path="/">
              {/* Trang chính */}
              {/* Các nội dung của trang chính ở đây */}
            </Route>
          </Switch>
        </Content>
        <Footer style={{ textAlign: 'center' }}>Ant Design ©2023 Created by BaoTrong</Footer>
      </Layout>
    </Router>
  );
}

export default App;
