import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Layout, Input, Button, List } from 'antd';
import './App.css'; // Import CSS để tùy chỉnh

const { Header, Content, Footer } = Layout;
function App() {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/notes');
        setNotes(response.data);
      } catch (error) {
        console.error('Lỗi tìm nạp ghi chú:', error);
      }
    };

    fetchData();
  }, []);

  const handleAddNote = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/notes', { text: newNote });
      setNotes([...notes, response.data]);
      setNewNote('');
    } catch (error) {
      console.error('Lỗi khi thêm ghi chú:', error);
    }
  };

  const handleDeleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:3001/api/notes/${id}`);
      const updatedNotes = notes.filter((note) => note._id !== id);
      setNotes(updatedNotes);
    } catch (error) {
      console.error('Lỗi xóa ghi chú:', error);
    }
  };

  const handleDeleteAllNotes = async () => {
    try {
      await axios.delete('http://localhost:3001/api/notes');
      setNotes([]);
    } catch (error) {
      console.error('Lỗi xóa tất cả ghi chú:', error);
    }
  };

  return (
    <Layout className="layout">
      <Header>
        <h1 style={{ color: 'white' }}>Lưu chú</h1>
      </Header>
      <Content className="content">
        <Input.TextArea
          placeholder="Thêm lưu chú mới"
          value={newNote}
          onChange={(e) => setNewNote(e.target.value)}
        />
        <Button onClick={handleAddNote} type="primary">Thêm</Button>
        <Button onClick={handleDeleteAllNotes} className="delete-button">Xoá Tất Cả</Button>
        <List
          dataSource={notes}
          renderItem={(note) => (
            <List.Item key={note._id}>
              {note.text}
              <Button onClick={() => handleDeleteNote(note._id)} type="danger">Xoá</Button>
            </List.Item>
          )}
        />
      </Content>
      <Footer style={{ textAlign: 'center' }}>Ant Design Custom Layout</Footer>
    </Layout>
  );
}

export default App;
