import React, { useState } from 'react';

function NoteForm({ onAddNote }) {
  const [text, setText] = useState('');

  const handleAddNote = () => {
    if (text.trim() !== '') {
      onAddNote(text);
      setText('');
    }
  };

  return (
    <div>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button onClick={handleAddNote}>Thêm lưu chú</button>
    </div>
  );
}

export default NoteForm;
