// Import các thư viện và component cần thiết

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { Form, Input, Button, List, Modal } from 'antd';

const NoteList = () => {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [editingNote, setEditingNote] = useState(null);
  const [editNoteContent, setEditNoteContent] = useState('');
  const [editNotePassword, setEditNotePassword] = useState(''); // Thêm trường mật khẩu
  const [sharedUsers, setSharedUsers] = useState([]); // Danh sách người dùng đã chia sẻ

  const username = localStorage.getItem('username');
  const token = localStorage.getItem('token');
  const decodedToken = jwt_decode(token);
  const userId = decodedToken.userId;

  useEffect(() => {
    axios
      .get(`http://localhost:3001/api/notes/${userId}`)
      .then((response) => {
        setNotes(response.data);
      })
      .catch((error) => {
        console.error('Không tìm nạp được ghi chú', error);
      });
  }, [userId]);

  const handleDeleteNote = (noteId) => {
    axios
      .delete(`http://localhost:3001/api/notes/${userId}/${noteId}`)
      .then(() => {
        setNotes((prevNotes) => prevNotes.filter((note) => note._id !== noteId));
      })
      .catch((error) => {
        console.error('Không thể xóa ghi chú', error);
      });
  };

  const handleAddNote = () => {
    axios
      .post(`http://localhost:3001/api/notes`, {
        userId,
        content: newNote,
        password: editNotePassword, // Lưu mật khẩu vào ghi chú mới
      })
      .then((response) => {
        setNotes((prevNotes) => [...prevNotes, response.data]);
        setNewNote('');
        setEditNotePassword(''); // Đặt lại mật khẩu sau khi thêm ghi chú
      })
      .catch((error) => {
        console.error('Không thể thêm ghi chú', error);
      });
  };

  const handleEditNote = (noteId, content, password) => {
    // Hỏi người dùng nhập mật khẩu để xem/chỉnh sửa ghi chú
    Modal.confirm({
      title: 'Nhập mật khẩu',
      content: (
        <Input
          type="password"
          value={editNotePassword}
          onChange={(e) => setEditNotePassword(e.target.value)}
        />
      ),
      onOk: () => {
        // Kiểm tra mật khẩu và cho phép chỉnh sửa nếu đúng
        if (password === editNotePassword) {
          setEditingNote(noteId);
          setEditNoteContent(content);
        } else {
          Modal.error({
            title: 'Sai mật khẩu',
            content: 'Mật khẩu không chính xác. Vui lòng thử lại.',
          });
        }
      },
    });
  };

  const handleSaveEditNote = () => {
    axios
      .put(`http://localhost:3001/api/notes/${userId}/${editingNote}`, {
        content: editNoteContent,
      })
      .then((response) => {
        setNotes((prevNotes) =>
          prevNotes.map((note) =>
            note._id === editingNote ? { ...note, content: editNoteContent } : note
          )
        );
        setEditingNote(null);
        setEditNoteContent('');
      })
      .catch((error) => {
        console.error('Không thể lưu chỉnh sửa ghi chú', error);
      });
  };

  const handleCancelEditNote = () => {
    setEditingNote(null);
    setEditNoteContent('');
    setEditNotePassword(''); // Đặt lại mật khẩu sau khi hủy bỏ chỉnh sửa
  };

  const handleShareNote = (noteId, sharedUserId) => {
    // Thực hiện tính năng chia sẻ ghi chú
    axios
      .post(`http://localhost:3001/api/notes/share`, {
        noteId,
        sharedUserId,
      })
      .then((response) => {
        setSharedUsers((prevSharedUsers) => [...prevSharedUsers, sharedUserId]);
      })
      .catch((error) => {
        console.error('Không thể chia sẻ ghi chú', error);
      });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    window.location.reload();
  };

  return (
    <div>
      <h2>Hi, {username} - Mời thí chủ test</h2>
      <Form onFinish={handleAddNote}>
        <Form.Item>
          <Input.TextArea
            type="text"
            placeholder="Nhập ghi chú mới"
            value={newNote}
            onChange={(e) => setNewNote(e.target.value)}
          />
        </Form.Item>
        <Form.Item>
          <Input
            type="password"
            placeholder="Mật khẩu ghi chú"
            value={editNotePassword}
            onChange={(e) => setEditNotePassword(e.target.value)}
          />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Thêm Ghi Chú
          </Button>
          <Button type="link" danger onClick={handleLogout}>
            Đăng Xuất
          </Button>
        </Form.Item>
      </Form>
      <List
        dataSource={notes}
        renderItem={(item) => (
          <List.Item
            actions={[
              <Button type="link" onClick={() => handleEditNote(item._id, item.content, item.password)}>
                Sửa
              </Button>,
              <Button type="link" onClick={() => handleDeleteNote(item._id)}>
                Xoá
              </Button>,
              <Button type="link" onClick={() => handleShareNote(item._id, sharedUserId)}>
                Chia Sẻ
              </Button>,
            ]}
          >
            {item.content} {item.password && "(Mật khẩu)"}
          </List.Item>
        )}
      />
      <Modal
        visible={editingNote !== null}
        title="Chỉnh Sửa Ghi Chú"
        onCancel={handleCancelEditNote}
        onOk={handleSaveEditNote}
      >
        <Input.TextArea
          value={editNoteContent}
          onChange={(e) => setEditNoteContent(e.target.value)}
        />
      </Modal>
    </div>
  );
};

export default NoteList;
