// NoteShare.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Để lấy tham số từ URL
import { Input, Button, Modal } from 'antd';

const NoteShare = () => {
  const { shareableLink } = useParams();
  const [password, setPassword] = useState(''); // Trạng thái mật khẩu
  const [noteContent, setNoteContent] = useState(''); // Nội dung ghi chú
  const [modalVisible, setModalVisible] = useState(false); // Hiển thị modal

  useEffect(() => {
    // Gửi yêu cầu GET để lấy nội dung của ghi chú dựa trên đường dẫn chia sẻ
    axios
      .get(`http://localhost:3001/api/share/${shareableLink}`, {
        params: { password },
      })
      .then((response) => {
        setNoteContent(response.data.content);
      })
      .catch((error) => {
        console.error('Không thể lấy nội dung ghi chú', error);
        // Hiển thị modal yêu cầu nhập mật khẩu nếu có lỗi
        setModalVisible(true);
      });
  }, [shareableLink, password]);

  const handleModalOk = () => {
    // Gửi lại yêu cầu GET sau khi nhập mật khẩu
    axios
      .get(`http://localhost:3001/api/share/${shareableLink}`, {
        params: { password },
      })
      .then((response) => {
        setNoteContent(response.data.content);
        setModalVisible(false);
      })
      .catch((error) => {
        console.error('Mật khẩu không hợp lệ', error);
      });
  };

  return (
    <div>
      <h2>Xem Ghi Chú Đã Được Chia Sẻ</h2>
      <div>
        <pre>{noteContent}</pre>
      </div>
      <Modal
        title="Nhập Mật Khẩu"
        visible={modalVisible}
        onOk={handleModalOk}
        onCancel={() => setModalVisible(false)}
      >
        <Input.Password
          placeholder="Nhập mật khẩu của ghi chú (nếu có)"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button type="primary" onClick={handleModalOk}>
          Xác nhận
        </Button>
      </Modal>
    </div>
  );
};

export default NoteShare;
