import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { Form, Input, Button, List, Modal } from 'antd';

const NoteList = () => {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState({ content: '', password: '' });
  const [editingNote, setEditingNote] = useState(null); // Ghi chú đang chỉnh sửa
  const [editNoteContent, setEditNoteContent] = useState(''); // Nội dung chỉnh sửa
  const [shareUsername, setShareUsername] = useState('');
  const [passwordModalVisible, setPasswordModalVisible] = useState(false);
  const [passwordModalContent, setPasswordModalContent] = useState('');

  // Lấy username từ localStorage
  const username = localStorage.getItem('username');

  // Lấy token từ localStorage
  const token = localStorage.getItem('token');

  // Giải mã token để lấy userId
  const decodedToken = jwt_decode(token);
  const userId = decodedToken.userId;

  useEffect(() => {
    // Gửi yêu cầu GET để lấy danh sách các ghi chú của người dùng
    axios
      .get(`http://localhost:3001/api/notes/${userId}`)
      .then((response) => {
        setNotes(response.data);
      })
      .catch((error) => {
        console.error('Không tìm nạp được ghi chú', error);
      });
  }, [userId]);

  const handleDeleteNote = (noteId) => {
    // Gửi yêu cầu DELETE để xoá ghi chú
    axios
      .delete(`http://localhost:3001/api/notes/${userId}/${noteId}`)
      .then(() => {
        // Cập nhật danh sách ghi chú sau khi xoá
        setNotes((prevNotes) => prevNotes.filter((note) => note._id !== noteId));
      })
      .catch((error) => {
        console.error('Không thể xóa ghi chú', error);
      });
  };

  const handleAddNote = () => {
    // Gửi yêu cầu POST để thêm ghi chú mới
    axios
      .post(`http://localhost:3001/api/notes`, {
        userId,
        content: newNote.content,
        password: newNote.password, // Gửi mật khẩu
      })
      .then((response) => {
        // Cập nhật danh sách ghi chú sau khi thêm ghi chú mới
        setNotes((prevNotes) => [...prevNotes, response.data]);
        // Xóa nội dung ghi chú và mật khẩu trong form
        setNewNote({ content: '', password: '' }); // Đặt lại mật khẩu
      })
      .catch((error) => {
        console.error('Không thể thêm ghi chú', error);
      });
  };

  const handleEditNote = (noteId, content) => {
    // Thiết lập trạng thái chỉnh sửa và nội dung chỉnh sửa
    setEditingNote(noteId);
    setEditNoteContent(content);
  };

  const handleSaveEditNote = () => {
    // Gửi yêu cầu PUT để lưu chỉnh sửa ghi chú
    axios
      .put(`http://localhost:3001/api/notes/${userId}/${editingNote}`, {
        content: editNoteContent,
      })
      .then((response) => {
        // Cập nhật danh sách ghi chú sau khi lưu chỉnh sửa
        setNotes((prevNotes) =>
          prevNotes.map((note) =>
            note._id === editingNote
              ? { ...note, content: editNoteContent }
              : note
          )
        );
        // Đóng modal chỉnh sửa
        setEditingNote(null);
        setEditNoteContent('');
      })
      .catch((error) => {
        console.error('Không thể lưu chỉnh sửa ghi chú', error);
      });
  };

  const handleCancelEditNote = () => {
    // Hủy bỏ chỉnh sửa
    setEditingNote(null);
    setEditNoteContent('');
  };

  const handleShareNote = (noteId) => {
    // Gửi yêu cầu PUT để chia sẻ ghi chú với người dùng cụ thể
    axios
      .put(`http://localhost:3001/api/notes/${userId}/${noteId}/share/${shareUsername}`)
      .then((response) => {
        // Cập nhật danh sách ghi chú sau khi chia sẻ
        setNotes((prevNotes) =>
          prevNotes.map((note) =>
            note._id === noteId ? { ...note, isShared: true } : note
          )
        );
        // Đặt lại trường nhập username
        setShareUsername('');
      })
      .catch((error) => {
        console.error('Không thể chia sẻ ghi chú', error);
      });
  };

  const showPasswordModal = (password) => {
    setPasswordModalContent(password);
    setPasswordModalVisible(true);
  };

  const hidePasswordModal = () => {
    setPasswordModalContent('');
    setPasswordModalVisible(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    // Làm mới trang sau khi đăng xuất
    window.location.reload();
  };

  return (
    <div>
      <h2>Hi, {username} - Mời thí chủ test</h2>

      <Form onFinish={handleAddNote}>
        <Form.Item>
          <Input.TextArea
            type="text"
            placeholder="Nhập ghi chú mới"
            value={newNote.content}
            onChange={(e) => setNewNote({ ...newNote, content: e.target.value })}
          />
        </Form.Item>
        <Form.Item>
          <Input
            type="text"
            placeholder="Mật khẩu (tùy chọn)"
            value={newNote.password}
            onChange={(e) => setNewNote({ ...newNote, password: e.target.value })}
          />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit">
            Thêm Ghi Chú
          </Button>
          <Button type="link" danger onClick={handleLogout}>
            Đăng Xuất
          </Button>
        </Form.Item>
      </Form>
      <List
        dataSource={notes}
        renderItem={(item) => (
          <List.Item
            actions={[
              <Button
                type="link"
                onClick={() => handleEditNote(item._id, item.content)}
              >
                Sửa
              </Button>,
              <Button
                type="link"
                onClick={() => handleDeleteNote(item._id)}
              >
                Xoá
              </Button>,
              item.isShared ? (
                <Button
                  type="link"
                  onClick={() => showPasswordModal(item.password)}
                >
                  Mật khẩu
                </Button>
              ) : (
                <Button
                  type="link"
                  onClick={() => handleShareNote(item._id)}
                >
                  Chia sẻ
                </Button>
              ),
            ]}
          >
            {item.content}
          </List.Item>
        )}
      />
      {/* Modal chỉnh sửa ghi chú */}
      <Modal
        visible={editingNote !== null}
        title="Chỉnh Sửa Ghi Chú"
        onCancel={handleCancelEditNote}
        onOk={handleSaveEditNote}
      >
        <Input.TextArea
          value={editNoteContent}
          onChange={(e) => setEditNoteContent(e.target.value)}
        />
      </Modal>
      {/* Modal mật khẩu ghi chú */}
      <Modal
        open={passwordModalVisible}
        title="Mật khẩu của ghi chú"
        onCancel={hidePasswordModal}
        footer={[
          <Button key="close" onClick={hidePasswordModal}>
            Đóng
          </Button>,
        ]}
      >
        {passwordModalContent}
      </Modal>
    </div>
  );
};

export default NoteList;
