import React, { useState, useEffect } from 'react';
import './App.css';
import NoteForm from './components/NoteForm';
import NoteList from './components/NoteList';
import Note from './components/Note';

function App() {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Lấy danh sách ghi chú từ sessionStorage khi ứng dụng được nạp lại
    const storedNotes = sessionStorage.getItem('notes');
    if (storedNotes) {
      setNotes(JSON.parse(storedNotes));
    }
  }, []);

  const addNote = (text) => {
    const newNote = { text };
    const newNotes = [...notes, newNote];
    setNotes(newNotes);

    // Lưu danh sách ghi chú vào sessionStorage sau khi thêm mới
    sessionStorage.setItem('notes', JSON.stringify(newNotes));
  };

  const deleteNote = (index) => {
    const newNotes = [...notes];
    newNotes.splice(index, 1);
    setNotes(newNotes);

    // Lưu danh sách ghi chú vào sessionStorage sau khi xoá
    sessionStorage.setItem('notes', JSON.stringify(newNotes));
  };
  const deleteAllNotes = () => {
    setNotes([]); // Xoá tất cả ghi chú
    sessionStorage.removeItem('notes'); // Xoá danh sách ghi chú trong sessionStorage
  };
  return (
    <div className="App">
      <h1>Lưu chú</h1>
      <NoteForm onAddNote={addNote} />
      <NoteList notes={notes} onDeleteNote={deleteNote} />
      <button onClick={deleteAllNotes}>Xoá tất cả</button> {/* Thêm nút để xoá tất cả */}
    </div>
  );
}

export default App;
