import React, { useState } from 'react';
import axios from 'axios';

function NoteForm() {
  const [text, setText] = useState('');

  const handleAddNote = () => {
    // Gửi yêu cầu POST đến endpoint để thêm lưu chú mới
    axios.post('/api/notes/add', { text })
      .then((response) => {
        setText('');
      })
      .catch((error) => {
        console.error('Lỗi khi thêm lưu chú:', error);
      });
  };

  return (
    <div>
      <h2>Thêm lưu chú mới</h2>
      <input
        type="text"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button onClick={handleAddNote}>Thêm lưu chú</button>
    </div>
  );
}

export default NoteForm;
