import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/auth/login', {
        username,
        password,
      });
      // Kiểm tra kết quả từ API và lưu thông tin đăng nhập (token, user) vào localStorage
      // Nếu thành công, chuyển hướng đến trang `/home`
      localStorage.setItem('token', response.data.token);
      // navigate('/'); // Sử dụng hook useNavigate để chuyển hướng
      <Link to="http://localhost:3000/"></Link>
    } catch (error) {
      console.error('Đăng nhập thất bại', error);
    }
  };

  return (
    <div>
      <h2>Đăng nhập</h2>
      <input
        type="text"
        placeholder="Tên đăng nhập"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Mật khẩu"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Đăng nhập</button>
    </div>
  );
};

export default Login;
