// NoteList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function NoteList() {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    // Gửi yêu cầu GET đến endpoint để lấy danh sách lưu chú
    axios.get('/api/notes/list')
      .then((response) => {
        setNotes(response.data);
      })
      .catch((error) => {
        console.error('Lỗi khi lấy danh sách lưu chú:', error);
      });
  }, []);

  const handleDeleteNote = (id) => {
    // Gửi yêu cầu DELETE đến endpoint để xoá lưu chú
    axios.delete(`/api/notes/delete/${id}`)
      .then((response) => {
        setNotes(notes.filter((note) => note._id !== id));
      })
      .catch((error) => {
        console.error('Lỗi khi xoá lưu chú:', error);
      });
  };

  return (
    <div>
      <h2>Danh sách lưu chú</h2>
      <ul>
        {notes.map((note) => (
          <li key={note._id}>
            {note.text}{' '}
            <button onClick={() => handleDeleteNote(note._id)}>Xoá</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default NoteList;
